# Optimized to run in O(n)! Wow!!!!
def character_name(char):
    global all_characters
    for c in all_characters:
        if c[0] == char: return c[1]
    return None

def stage_name(stage):
    global all_stages
    for s in all_stages:
        if s[0] == stage: return s[1]
    return None

def stage_filename(stage):
    global all_stages
    for s in all_stages:
        if s[0] == stage: return s[2]
    return None

def setupRecords():
    global myWinLoss, oppWinLoss, myKillDeath, myMatchups
    myWinLoss = dict()
    oppWinLoss = dict()
    myKillDeath = dict()
    myMatchups = dict()
    #setup dictionary for all datasets
    for c in all_characters:
        if c[1] != "nana" and c[1] != "popo":
            #####win/loss data#####
            myWinLoss[c[1]] = {}
            myWinLoss[c[1]]["wins"] = 0
            myWinLoss[c[1]]["losses"] = 0
            oppWinLoss[c[1]] = {}
            oppWinLoss[c[1]]["wins"] = 0
            oppWinLoss[c[1]]["losses"] = 0
            ######kill/death data##############
            myKillDeath[c[1]] = {}
            myKillDeath[c[1]]["kills"] = 0
            myKillDeath[c[1]]["deaths"] = 0

            ####matchup data####
            myMatchups[c[1]] = {}
            for x in all_characters:
                myMatchups[c[1]][x[1]] = {}
                myMatchups[c[1]][x[1]]["wins"] = 0
                myMatchups[c[1]][x[1]]["losses"] = 0

